package com.example.mobile_app_project.data.remote

import com.example.mobile_app_project.data.remote.dto.ForecastResponse
import com.example.mobile_app_project.data.remote.dto.GeocodingResponse
import retrofit2.http.GET
import retrofit2.http.Query

interface OpenMeteoApiService {

    // Geocoding API: https://geocoding-api.open-meteo.com/v1/search
    @GET("/v1/search")
    suspend fun searchCity(
        @Query("name") name: String,
        @Query("count") count: Int = 1
    ): GeocodingResponse

    // Forecast API: https://api.open-meteo.com/v1/forecast
    @GET("/v1/forecast")
    suspend fun getForecast(
        @Query("latitude") latitude: Double,
        @Query("longitude") longitude: Double,
        // Request both current and hourly variables per docs
        @Query("current") current: String = "temperature_2m,wind_speed_10m",
        @Query("hourly") hourly: String = "time,temperature_2m,relative_humidity_2m,wind_speed_10m",
        @Query("timezone") timezone: String = "auto"
    ): ForecastResponse
}

package com.example.mobile_app_project.data.remote

import com.example.mobile_app_project.data.remote.dto.ForecastResponse
import com.example.mobile_app_project.data.remote.dto.GeocodingResponse
import retrofit2.http.GET
import retrofit2.http.Query

interface OpenMeteoApiService {

    // Geocoding API: https://geocoding-api.open-meteo.com/v1/search
    @GET("/v1/search")
    suspend fun searchCity(
        @Query("name") name: String,
        @Query("count") count: Int = 1
    ): GeocodingResponse

    // Forecast API: https://api.open-meteo.com/v1/forecast
    @GET("/v1/forecast")
    suspend fun getForecast(
        @Query("latitude") latitude: Double,
        @Query("longitude") longitude: Double,
        // Request both current and hourly variables per docs
        @Query("current") current: String = "temperature_2m,wind_speed_10m",
        @Query("hourly") hourly: String = "temperature_2m",
        @Query("timezone") timezone: String = "auto"
    ): ForecastResponse
}

package com.example.mobile_app_project.data.remote.dto

import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

@Serializable
data class CurrentWeather(
    val time: String,
    @SerialName("temperature_2m") val temperature2m: Double? = null,
    @SerialName("wind_speed_10m") val windSpeed10m: Double? = null
)

@Serializable
data class HourlyWeather(
    val time: List<String> = emptyList(),
    @SerialName("temperature_2m") val temperature2m: List<Double> = emptyList(),
    @SerialName("relative_humidity_2m") val relativeHumidity2m: List<Double> = emptyList(),
    @SerialName("wind_speed_10m") val windSpeed10m: List<Double> = emptyList()
)

@Serializable
data class ForecastResponse(
    @SerialName("current") val current: CurrentWeather? = null,
    @SerialName("hourly") val hourly: HourlyWeather? = null
)

package com.example.mobile_app_project.data.remote.dto

import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

@Serializable
data class Hourly(
    val time: List<String> = emptyList(),
    @SerialName("temperature_2m") val temperature2m: List<Double> = emptyList()
)

@Serializable
data class ForecastResponse(
    val latitude: Double,
    val longitude: Double,
    val elevation: Double,
    @SerialName("utc_offset_seconds") val utcOffsetSeconds: Int,
    val hourly: Hourly? = null
)
